function iPad(x) {
	const parallax = document.querySelector('#contact')
	const s1 = document.querySelector('#s1')
	const contact = document.querySelector('#contact-text')
	const about = document.querySelector('#about')

	if (x.matches) {
		parallax.style.filter = "brightness(0.3)";
		s1.style.filter = "brightness(0.6)";
		contact.classList.remove('text-left');
		contact.classList.add('text-center', 'pt-5');
		about.classList.remove('text-right');
		about.classList.add('text-center', 'pl-5', 'pt-5');
	} else {
		return;
	}
}

const x = window.matchMedia("(min-width: 768px)")
iPad(x)
x.addListener(iPad)


function iPhone(y) {
	const slides = document.querySelector('#slides')

	if (y.matches) {
		slides.classsList.remove('w-100')
	} else {
		return;
	}

}

const y = window.matchMedia("(min-width: 375px)")
iPhone(y)
y.addListener(iPhone)
