const parallax = document.querySelector('#contact')
const s1 = document.querySelector('#s1')
const s1A = document.querySelector('#s1-a')
const s1B = document.querySelector('#s1-b')
const s1C = document.querySelector('#s1-c')

const y = window.matchMedia('(max-width: 376px)')
iPhone(y)
y.addListener(iPhone)

function iPhone(y) {
	const slides = document.querySelector('#slides')
	const s1Left = document.querySelector('#s1-left')
	const secondCard = document.querySelector('#second-card')
	const s1Right = document.querySelector('.s1-text-size')
	const s1Text = document.querySelector('#s1-text')
	const bottomP = document.querySelector('#bottom-p')

	if (y.matches) {
		parallax.style.filter = 'brightness(0.3)'
		s1Text.classList.add('small-font')
		s1A.classList.remove('display-1')
		s1B.classList.remove('display-2')
		s1C.classList.remove('display-2')
		s1Left.classList.remove('pt-4', 'mt-5')
		parallax.style.height = null
		parallax.style.height = '1099px'
		secondCard.classList.remove('ml-5', 'mr-5')
		s1Right.textContent = "Catering small to medium size businesses with a focus on independent freelancers. We offer solutions for optimal IT performance."
		bottomP.textContent = ""
	} else {
		return;
	}
}


const x = window.matchMedia('(max-width: 769px)')
iPad(x)
x.addListener(iPad)

function iPad(x) {
	const contact = document.querySelector('#contact-text')
	const about = document.querySelector('#about')

	if (x.matches) {
		parallax.style.filter = 'brightness(0.3)';
		s1.style.filter = 'brightness(0.6)';
		contact.classList.remove('text-left');
		contact.classList.add('text-center', 'pt-5');
		about.classList.remove('text-right');
		about.classList.add('text-center', 'pl-5', 'pt-5');
	} else {
		return;
	}
}


const z = window.matchMedia('(min-width: 1488px)')
full(z)
z.addListener(full)

function full(z) {
	if (z.matches) {
		s1A.classList.add('display-1')
		s1B.classList.add('display-2')
		s1C.classList.add('display-2')
	} else {
		return;
	}
}
